apiVersion: v1
kind: ConfigMap
metadata:
  name: {{ include "alert-label-receiver.fullname" . }}-config
  namespace: {{ .Values.namespace }}
  labels:
    {{- include "alert-label-receiver.labels" . | nindent 4 }}
data:
  config.yaml: |
    account:
{{- range .Values.config.account }}
      - {{ . | quote }}
{{- end }}
    label: {{ .Values.config.label }}

apiVersion: apps/v1
kind: Deployment
metadata:
  name: {{ include "alert-label-receiver.fullname" . }}
  namespace: {{ .Values.namespace }}
  labels:
    {{- include "alert-label-receiver.labels" . | nindent 4 }}
spec:
  replicas: {{ .Values.replicas }}
  selector:
    matchLabels:
      {{- include "alert-label-receiver.selectorLabels" . | nindent 6 }}
  template:
    metadata:
      labels:
        {{- include "alert-label-receiver.selectorLabels" . | nindent 8 }}
    spec:
      serviceAccountName: {{ .Values.serviceAccount.name }}
      securityContext:
        runAsNonRoot: {{ .Values.security.runAsNonRoot }}
        runAsUser: {{ .Values.security.runAsUser }}
        fsGroup: {{ .Values.security.fsGroup }}
      containers:
        - name: manager
          image: "{{ .Values.image.repository }}:{{ .Values.image.tag | default .Chart.AppVersion }}"
          imagePullPolicy: {{ .Values.image.pullPolicy }}
          ports:
            - containerPort: {{ .Values.service.targetPort }}
              name: webhook
          securityContext:
            allowPrivilegeEscalation: {{ .Values.security.allowPrivilegeEscalation }}
            capabilities:
              drop:
                - ALL
            readOnlyRootFilesystem: {{ .Values.security.readOnlyRootFilesystem }}
          resources:
            {{- toYaml .Values.resources | nindent 12 }}
          volumeMounts:
            - name: config
              mountPath: /config.yaml
              subPath: config.yaml
              readOnly: true
      volumes:
        - name: config
          configMap:
            name: {{ include "alert-label-receiver.fullname" . }}-config

